import Awards from "./Awards"
import Education from "./Education"
import Pricing from "./Pricing";
import Stats from "./Stats";
import Hero from "./Hero";
import Navbar from "../Navbar";
import Footer from "../Footer";
import OpenAccount from "../OpenAccount";
import { useLinkClickHandler, useLocation } from 'react-router-dom'
import * as React from 'react';
import Alert from '@mui/material/Alert';
import AlertTitle from '@mui/material/AlertTitle';
import { Collapse } from "@mui/material";


function HomePage() {

  React.useEffect(() => {
    const queery = new URLSearchParams(window.location.search)
    const name = queery.get('name');
    const message = queery.get('message');
    console.log("Name : ", name);
    console.log("Message : ", message);
  })

  const location = useLocation();
  const [showAlert, setShowAlert] = React.useState(true);
  const formData = location.state;
  console.log("Hero Secn form Data State : ", formData)

  React.useEffect(() => {
    const timer = setTimeout(() => setShowAlert(false), 5000);
    return () => clearTimeout(timer); // cleanup on unmount
  }, []);

  return (
    <>
      <Collapse in={showAlert}>
        {(formData?.name || formData?.message) && (
          <Alert severity="success" sx={{ mt: 2 }}>
            <AlertTitle>ZerodhaReplica Says</AlertTitle>
            Welcome , {formData?.name && <>{formData.name}</>}
            {formData?.message && <><br />{formData.message}</>}
          </Alert>
        )}
      </Collapse>





      <Hero />
      <Awards />
      <Stats />
      <Pricing />
      <Education />
      <OpenAccount />
    </>
  );
}

export default HomePage;
