import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  Paper,
  Alert,
  AlertTitle,
  Collapse,
} from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [showAlert, setShowAlert] = React.useState(true);
  const [alertMessage, setAlertMessage] = useState('');
  React.useEffect(() => {
    const timer = setTimeout(() => setShowAlert(false), 5000);
    return () => clearTimeout(timer); // cleanup on unmount
  }, []);

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
  };

  const validate = () => {
    let newErrors = {};
    if (!formData.username) newErrors.username = 'Username is required';
    if (formData.password.length < 6)
      newErrors.password = 'Password must be at least 6 characters';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    try {
      const res = await axios.post('http://localhost:3002/login', formData);
      console.log('Login successful', res);

      const name = encodeURIComponent(res.data.user.name);
      const message = encodeURIComponent(res.data.message);

      window.location.href = `http://localhost:3001?name=${name}&message=${message}`;
    } catch (err) {
      console.error('Login error:', err.response?.data || err.message);

      // Trigger UI state for error display
      setAlertMessage(err.response?.data || err.message);
      setShowAlert(true);
console.log(alertMessage)
      navigate('/login');
    }
  };


  return (
    <>
    {showAlert.length && (
        <Collapse in={showAlert}>
          <Alert severity="error" sx={{ mt: 2 }}>
            <AlertTitle>ZerodhaReplica Says</AlertTitle>
            {alertMessage}
          </Alert>
        </Collapse>
      )}
    <Container maxWidth="sm">
            <Paper elevation={3} sx={{ padding: 4, mt: 8 }}>
              
        <Typography variant="h5" gutterBottom align="center">
          Login
        </Typography>
        <Box component="form" onSubmit={handleSubmit} noValidate>
          <TextField
            label="Username"
            name="username"
            fullWidth
            value={formData.username}
            onChange={handleChange}
            error={!!errors.username}
            helperText={errors.username}
            sx={{ mt: 2 }}
          />
          <TextField
            label="Password"
            name="password"
            type="password"
            fullWidth
            value={formData.password}
            onChange={handleChange}
            error={!!errors.password}
            helperText={errors.password}
            sx={{ mt: 2 }}
          />
          <Button variant="contained" type="submit" fullWidth sx={{ mt: 3 }}>
            Login
          </Button>
        </Box>
      </Paper>
    </Container>
    </>
  );
};

export default Login;
