import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box, Grid, Paper, } from '@mui/material';
import axios from "axios"
import { useNavigate } from "react-router-dom";

import { useEffect } from 'react';

const Login = () => {
  const [formData, setFormData] = useState({
    name: '',
    password: '',
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
  };

  const validate = () => {
    let newErrors = {};
    if (!formData.name) newErrors.name = 'Name is required';
    if (formData.password.length < 6)
      newErrors.password = 'Password must be at least 6 characters';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   if (!validate()) return;
  //   console.log('Login data:', formData);
  //   axios.post("http://localhost:3002/login", formData)
  //     .then((res) => {
  //       console.log("Axios is Send request for Login Route")
  //       // res.headers(formData);
  //       navigate("/", { state: formData });

  //     }).catch((err) => {
  //       console.log("Error In Axios Login Route : ", err)
  //       navigate("/error")
  //     })
  //   // You can trigger your API call here
  // };

  const handleSubmit = async (e) => {
  e.preventDefault();
  if (!validate()) return;

  try {
    const res = await axios.post("http://localhost:3002/login", formData);
    console.log("Login successful", res);
    navigate("/", { state: formData });
  } catch (err) {
    console.error("Login error:", err.response?.data || err.message);
    navigate("/error");
  }
};


  return (
    <Container maxWidth="sm">
      <Paper elevation={3} sx={{ padding: 4, mt: 8 }}>
        <Typography variant="h5" gutterBottom align="center">
          Logged in
        </Typography>
        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={2}>

            <Grid item xs={12}>
              <TextField label="Full Name" name="name" fullWidth value={formData.name} onChange={handleChange} error={!!errors.name} helperText={errors.name} />
            </Grid>
            
            <Grid item xs={12}>
              <TextField label="Password" name="password" type="password" fullWidth value={formData.password} onChange={handleChange} error={!!errors.password} helperText={errors.password} />
            </Grid>
            <Grid item xs={12}>
              <Button variant="contained" type="submit" fullWidth sx={{ mt: 1 }} > Register </Button>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default Login;
