import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box, Grid, Paper, } from '@mui/material';
import axios from "axios"
import { useNavigate } from "react-router-dom";

import { useEffect } from 'react';

const Signup = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
  };

  const validate = () => {
    let newErrors = {};
    if (!formData.name) newErrors.name = 'Name is required';
    if (!formData.email.includes('@')) newErrors.email = 'Enter a valid email';
    if (formData.password.length < 6)
      newErrors.password = 'Password must be at least 6 characters';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  //  useEffect(() => {
  //   axios.post("http://localhost:3002/signup")
  //   .then((res) => {
  //     console.log("Axios is Send request for Signup Route")
  //     res.headers(formData);
  //   }) .catch((err) => {
  //     console.log("Error In Axios Signup Route : " , err)
  //   })
  // } ,[])
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;
    console.log('Signup data:', formData);
    axios.post("http://localhost:3002/signup", formData)
      .then((res) => {
        console.log("Axios is Send request for Signup Route")
        // res.headers(formData);
        navigate("/", { state: formData });

      }).catch((err) => {
        console.log("Error In Axios Signup Route : ", err)
        navigate("/about")
      })
    // You can trigger your API call here
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={3} sx={{ padding: 4, mt: 8 }}>
        <Typography variant="h5" gutterBottom align="center">
          Sign Up
        </Typography>
        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField label="Full Name" name="name" fullWidth value={formData.name} onChange={handleChange} error={!!errors.name} helperText={errors.name} />
            </Grid>
            <Grid item xs={12}>
              <TextField label="Email" name="email" type="email" fullWidth value={formData.email} onChange={handleChange} error={!!errors.email} helperText={errors.email} />
            </Grid>
            <Grid item xs={12}>
              <TextField label="Password" name="password" type="password" fullWidth value={formData.password} onChange={handleChange} error={!!errors.password} helperText={errors.password} />
            </Grid>
            <Grid item xs={12}>
              <Button variant="contained" type="submit" fullWidth sx={{ mt: 1 }} > Register </Button>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default Signup;
